const puppeteer = require('puppeteer');
const fs = require('fs');

(async () => {
    const browser = await puppeteer.launch();
    const page = await browser.newPage();

    const svg = fs.readFileSync('level20.svg', 'utf8');
    await page.setContent(`<html><body style="margin:0">${svg}</body></html>`, {
        waitUntil: 'load'
    });

    // Adjust size for resolution
    await page.setViewport({ width: 12000, height: 4000 });
    await page.screenshot({ path: 'level20.png', fullPage: true });

    await browser.close();
    console.log('âœ… PNG created: level20.png');
})();
